mapper.select:
Select
  u.tenantid AS 'tenantId'
  ,u.userEmail AS 'userEmail'
  ,u.userPswd AS 'userPswd'
  ,u.phone_no AS 'phoneNo'
  ,u.user_name AS 'userName'
  ,t.tenantName AS 'tenantName'
  from   users u,tenant t where t.tenantid=u.tenantid
  ---下一句话的意思是: 至少有一个不是space或者null
  and (isnull(u.userEmail,'')!='' or   isnull(u.phone_no,'')!='' or  isnull(u.user_name,'')!='')
  and isnull(u.tenantid,'')=#{info.tenantId}
  and isnull(t.tenantid,'')=#{info.tenantId}
  and
  (
      isnull(u.userEmail,'')=#{info.userEmail}
      or isnull(u.phone_no,'')=#{info.phoneNo}
      or isnull(u.user_name,'')=#{info.userName}
  )

Select top 20 a.s_dd --as sDd,---价格创建日期 ,a.up --as up ,--价格 ,a.qty --as qty ,--数量 ,a.HJ_NO---单位 ,a.cur_id ---as curId,----币别代号 ,a.bil_type----01代表不含运费的单价 ,a.rem---备注,打样系统的会存为"打样系统" ,a.price_id----1代表销售价格, 2代表采购价格 ,b.name ----as curName ----币别名字 --- ,isnull(a.OLEFIELD,'') as olefield---是页面四条数据保存的时候关联同一次的字段 ,isnull(a.OLEFIELD,'') as dingJiaGuanLian--是页面四条数据保存的时候关联同一次的字段 from up_def a LEFT JOIN prdt d ON d.prd_no=a.prd_no LEFT JOIN cur_id b ON a.cur_id=b.cur_id where a.prd_no=#{prdNo,jdbcType=VARCHAR} AND a.OLEFIELD like '%SamplesSys%' AND a.price_id=#{priceId} AND a.cur_id=#{CurId} AND a.bil_type=#{bilType}
GROUP BY a.s_dd --as sDd,---价格创建日期 ,a.up --as up ,--价格 ,a.qty --as qty ,--数量 ,a.HJ_NO---单位 ,a.cur_id ---as curId,----币别代号 ,a.bil_type----01代表不含运费的单价 ,a.rem---备注,打样系统的会存为"打样系统" ,a.price_id----1代表销售价格, 2代表采购价格 ,b.name ----as curName ----币别名字 --- ,isnull(a.OLEFIELD,'') as olefield---是页面四条数据保存的时候关联同一次的字段 ,isnull(a.OLEFIELD,'')
ORDER BY a.s_dd DESC

select a.olefield from
(
   select top 80 olefield from
   up_def where
   prd_no=#{prdNo}
   and price_id=#{priceId}
   and olefield like #{olefield}

        and olefield not in

            #{item}


   order by s_dd DESC
) a
group by a.olefield
